mapper.select:
select cc.*,c.name as communityname from tb_common_config cc,tb_wp_community c where cc.refid = c.uuid and cc.refid is not null and cc.refid!=''
and cc.bindid = #{config.bindid}
and cc.refid = #{config.refid}
and cc.configname like concat("%", #{config.configname},"%")
and cc.refid in (select c.uuid from tb_platform_user a, tb_wp_user_com b, tb_wp_community c where a.uuid = b.userid and b.communityid = c.uuid and a.uuid = #{userid} and (c.parentCommunity is null or c.parentCommunity != '000000') )
order by cc.refid ;

select distinct configvalue
from tb_common_config c
;

delete
from tb_common_config
where configname = #{config.configname}

    and refid = #{config.refid}

;

select c.* from tb_common_config c
where c.configname=#{configname}
and c.refid=#{refid}

select c.* from tb_common_config c
where c.configname=#{configname}
and c.bindid=#{bindid}
and c.refid is null
