mapper.select:
select ma.*
from tb_mtx_activity ma
where ma.bindid=#{mtxActivity.bindid}

    and ma.merchantid =#{mtxActivity.merchantid}


    and ma.name like concat("%",#{mtxActivity.name},"%")


    and ma.status =#{mtxActivity.status}



            and DATE_FORMAT(ma.startdate, '%Y-%m-%d %H:%i:%S')>= #{mtxActivity.startdate}




            and DATE_FORMAT(ma.enddate, '%Y-%m-%d %H:%i:%S') <= #{mtxActivity.enddate}


order by ma.createon desc;

select ma.* from tb_mtx_activity ma where ma.merchantid in
#{item.uuid}
order by ma.createon desc
