mapper.select:
select map.* ,wu.name as name ,wu.headimgurl as headimg ,wu.nickname as nickname ,wu.contactno as contactno
from tb_mtx_activity_participant  map
inner join tb_wp_user wu on wu.uuid=map.userid
where map.activityid=#{participant.activityid}

    and map.status=#{participant.status}


    and wu.name=#{participant.name}


    and wu.contactno=#{participant.contactno}

order by map.createon desc;

select map.* ,wu.name as name ,wu.headimgurl as headimg ,wu.nickname as nickname ,wu.contactno as contactno
from tb_mtx_activity_participant  map
inner join tb_wp_user wu on wu.uuid=map.userid
where map.activityid=#{participant.activityid}

    and map.status is null

order by map.createon desc;
