mapper.select:
select *
from tb_mtx_merchant
where bindid = #{merchant.bindid}
order by modifyon desc;

select c.* from tb_mtx_merchant c
where c.name = #{merchant.name}

    and c.uuid != #{merchant.uuid}

;

select c.*
from tb_platform_user a, tb_mtx_user_merchant b, tb_mtx_merchant c
where a.uuid = b.userid
and   b.merchantid = c.uuid
and   a.uuid = #{userid}
order by c.createon;

select c.*
from tb_mtx_merchant c
where bindid = #{merchant.bindid}

    and c.name  like concat("%",#{merchant.name},"%")


    and c.address  like concat("%",#{merchant.address},"%")


    and c.legalperson  like concat("%",#{merchant.legalperson},"%")


    and c.uuid in (select c.uuid
    from tb_platform_user a, tb_mtx_user_merchant b, tb_mtx_merchant c
    where a.uuid = b.userid
    and   b.merchantid = c.uuid
    and   a.uuid = #{userid}
    )

order by modifyon desc;

select distinct c.*
from tb_mtx_merchant c
where c.bindid = #{bindid}
and c.uuid not in (select uc.merchantid from tb_mtx_user_merchant uc where uc.userid = #{userid})
order by c.createon;

select mt.*
from tb_mtx_merchant mt
inner join tb_mtx_order mo on mo.merchantid = mt.uuid
inner join tb_mtx_machine me on me.orderid = mo.uuid
where me.machinemodel = #{machine.machinemodel}
and me.machineno = #{machine.machineno}
and me.engineno = #{machine.engineno}
and me.type = 'MACHINE'
