mapper.select:
select mc.*,wu.nickname as nickname,wu.headimgurl as headimgurl
from tb_mtx_consult mc
left join tb_wp_user wu on wu.uuid=mc.userid
where TRUE

    and mc.identify =#{mtxConsult.identify}


    and mc.status =#{mtxConsult.status}


    and wu.nickname like concat("%",#{mtxConsult.nickname},"%")

order by mc.createon desc;
