mapper.select:
select mlp.* ,wu.name as name ,wu.headimgurl as headimg ,wu.nickname as nickname ,wu.contactno as contactno
from tb_mtx_lucky_participant  mlp
inner join tb_wp_user wu on wu.uuid=mlp.userid
where mlp.activityid=#{mtxLuckyParticipant.activityid}

    and mlp.status='WAIT_WIN'


    and mlp.status='WIN'


    and mlp.userid=#{mtxLuckyParticipant.userid}

order by mlp.createon desc;
