mapper.select:
select mr.*,mp.model as model,mm.name as merchantname
from tb_mtx_reserve mr
left join tb_mtx_product mp on mp.uuid=mr.productid
left join tb_mtx_merchant mm on mm.uuid=mr.merchantid
where TRUE

    and mr.name like concat("%",#{mtxReserve.name},"%")


    and mr.phone like concat("%",#{mtxReserve.phone},"%")


    and mr.status =#{mtxReserve.status}


    and mr.merchantid =#{mtxReserve.merchantid}


    and mr.uuid =#{mtxReserve.uuid}

order by mr.modifyon desc;

select mr.*,mp.model as model,mm.name as merchantname
from tb_mtx_reserve mr
inner join tb_mtx_merchant mm on mm.uuid=mr.merchantid
left join tb_mtx_product mp on mp.uuid=mr.productid
where mm.bindid=#{bindid}
and mm.uuid in

    #{item}


    and mr.name like concat("%",#{mtxReserve.name},"%")


    and mr.phone like concat("%",#{mtxReserve.phone},"%")


    and mr.status =#{mtxReserve.status}


    and mr.merchantid =#{mtxReserve.merchantid}

order by mr.modifyon desc;

select mr.*,mp.model as model,mm.name as merchantname
from tb_mtx_reserve mr
left join tb_mtx_product mp on mp.uuid=mr.productid
left join tb_mtx_merchant mm on mm.uuid=mr.merchantid
where mr.uuid =#{mtxReserve.uuid}
order by mr.modifyon desc;
