mapper.select:
select mu.*,concat(mm.machinemodel,mm.machinename) as name
from tb_mtx_usermachine mu
left join tb_mtx_machine mm on mm.uuid=mu.machineid
where mu.userid=#{userid}
and mu.type=#{type}
order by date_format(mu.createon,'%Y-%m-%d %H:%i:%s') desc;

select mu.*,wu.name as name,mm.machinename as machinename from tb_mtx_usermachine mu left join tb_mtx_machine mm on mm.uuid=mu.machineid left join tb_wp_user wu on wu.uuid=mu.userid where true
and wu.name like concat("%", #{mtxUserMachine.name},"%")
and mm.machinename like concat("%", #{mtxUserMachine.machinename},"%")
and mu.type = #{mtxUserMachine.type}
order by date_format(mu.createon,'%Y-%m-%d %H:%i:%s') desc;
