mapper.select:
select qm.*
from tb_mtx_quality_management qm
where
1=1

    and qm.snno like concat("%",#{qualityMgmt.snno},"%")


    and qm.type = #{qualityMgmt.type}



                      and qm.servicedt >= #{startTime}




                      and qm.servicedt <= #{endTime}


order by qm.modifyon desc
