mapper.select:
select mt.*,mm.name as merchantname
from tb_mtx_train mt
inner join tb_mtx_merchant mm on mt.merchantid = mm.uuid
where
1=1

    and mt.status = #{train.status}


    and mt.merchantid = #{train.merchantid}


    and mt.snno  like concat("%",#{train.snno},"%")


    and mt.machinemodel = #{train.machinemodel}


    and mt.machineno like concat("%",#{train.machineno},"%")


    and mt.personphone like concat("%",#{train.personphone},"%")



                      and mt.traindt >= #{startTime}




                      and mt.traindt <= #{endTime}


order by mt.modifyon desc

select mt.* from tb_mtx_train mt inner join tb_mtx_merchant mm on mt.merchantid = mm.uuid and mm.uuid in
#{item.uuid}
order by mt.modifyon desc
