mapper.select:
select mt.*
from tb_mtx_train mt
where
1=1

    and mt.machinemodel = #{train.machinemodel}


    and mt.machineno like concat("%",#{train.machineno},"%")


    and mt.personphone like concat("%",#{train.personphone},"%")



                      and mt.traindt >= #{startTime}




                      and mt.traindt <= #{endTime}


order by mt.modifyon desc
